mapper.update:
UPDATE coupon_info


        coupon_name = #{couponName}


        coupon_cost = #{couponCost}


        sill = #{sill}


        `value` = #{value}


        expiration_time = #{expirationTime}


        remaining = #{remaining}


WHERE coupon_id = #{couponId}

mapper.select:
SELECT * FROM coupon_info
WHERE coupon_type = #{goodType}
LIMIT #{i}, #{pageSize}

SELECT * FROM coupon_info
WHERE coupon_type = #{goodType} and use_store_id = #{merchantId}
LIMIT #{i}, #{pageSize}

SELECT COUNT(*) FROM coupon_info
WHERE coupon_type = #{goodType} and use_store_id = #{merchantId}
LIMIT #{i}, #{pageSize}

SELECT * FROM commodity_info


        AND commodity_name LIKE CONCAT('%', #{commodityName}, '%')


        AND commodity_type = #{commodityType}


LIMIT #{i}, #{pageSize}

SELECT COUNT(*) FROM commodity_info


        AND commodity_name LIKE CONCAT('%', #{commodityName}, '%')


        AND commodity_type = #{commodityType}


LIMIT #{i}, #{pageSize}

SELECT * FROM coupon_info


        AND coupon_name LIKE CONCAT('%', #{couponName}, '%')


        AND coupon_type = #{couponType}


        AND use_type = #{useType}


        AND use_store_id = #{useStoreId}


LIMIT #{i}, #{pageSize}

SELECT COUNT(*) FROM coupon_info


        AND coupon_name LIKE CONCAT('%', #{couponName}, '%')


        AND coupon_type = #{couponType}


        AND use_type = #{useType}


        AND use_store_id = #{useStoreId}


LIMIT #{i}, #{pageSize}

mapper.update:
update second_hand_good_info set
buyer_id = #{buyerId} and
delivery_id = #{deliveryId}
where good_id = #{goodId}
