mapper.update:
UPDATE user_basic t SET
t.carbon_credits_month = (SELECT carbon_credits_month FROM carbon_credits_basic WHERE user_id = t.user_id),
t.user_level = #{level}
WHERE user_id = #{userId}

mapper.select:
select b.* from (
SELECT t.*, @rownum := @rownum + 1 AS user_rank
FROM (SELECT @rownum := 0) r, (SELECT * FROM user_basic ORDER BY carbon_credits_month ASC ) AS t
) as b where user_id = #{userId};
